import { useEffect, useState } from 'react';
import { auth, firestore } from './../firebase';
import Chat from './Chat';
import { Accordion, Card, Button, ListGroup } from 'react-bootstrap'
import stepsStart from '../logic/chatStart';
import ChatCreate from './ChatCreate';

const Account = (props) => {
    const handleLogout = () => {
        setId(0)
        auth.signOut();
      };
    const [name, setName] = useState('');
    const [status, setStatus] = useState('');
    const [newAccount, setNewAccount] = useState(true);
    // const [name, setName] = useState('');
    // const [name, setName] = useState('');
    // const [name, setName] = useState('');
    // const [name, setName] = useState('');
    const [userCurrent, setUserCurrent] = useState('');
    const {
        id,
        setId,
    } = props;

    const getDateFromUser = (data) => {
        setUserCurrent(data)
        setName(data.displayName);
        if(data.new) {
            setNewAccount(true);
        } else {
            setNewAccount(false);
        }
    }

    const getUser = () => {
        firestore.collection('users')
            .get()
            .then(snapshot => {
                snapshot.forEach(doc => {
                    if(doc.id === id) {
                        const data = doc.data();
                        getDateFromUser(data);
                    }
                })
            })
            .catch(error => console.log(error))
    }

    useEffect(() => {
        getUser()
      }, []);

    return (
        <section className = 'hero'>
            <nav>
                <h2>Welcome {userCurrent.displayName}</h2>
                <button onClick = {handleLogout}>Logout</button>
            </nav>
            <section className ='col-sm-8 nopadding'>
                <Card className = 'rowInfo col-sm-12 nopadding'>
                    <Card className = "col-sm-3">
                        <Card.Img variant="button" width="100%" src='avatar.jpeg' />
                        <Card.Body>
                            <Card.Title>{name}</Card.Title>
                            <Card.Text>
                            </Card.Text>
                        </Card.Body>
                    </Card>
                    <Card className = "col-sm-9 white">
                        <Accordion>
                            <Card>
                                <Card.Header>
                                    <Accordion.Toggle as={Button} variant="link" eventKey="0">
                                        Info about user
                                    </Accordion.Toggle>
                                </Card.Header>
                                <Accordion.Collapse eventKey="0">
                                    <Card.Body className ="col-sm-12">
                                        <ListGroup className ="col-sm-6 nopadding">
                                            <ListGroup.Item>Status:</ListGroup.Item>
                                            <ListGroup.Item>Job Function:</ListGroup.Item>
                                            <ListGroup.Item>Primary Skills:</ListGroup.Item>
                                            <ListGroup.Item>Key Skills:</ListGroup.Item>
                                            <ListGroup.Item>Level:</ListGroup.Item>
                                        </ListGroup>
                                        <ListGroup className ="col-sm-6 nopadding"> 
                                            <ListGroup.Item>{userCurrent.status}</ListGroup.Item>
                                            <ListGroup.Item>{userCurrent.profession}</ListGroup.Item>
                                            <ListGroup.Item>{userCurrent.duration}</ListGroup.Item>
                                            <ListGroup.Item>{userCurrent.primary}</ListGroup.Item>
                                            <ListGroup.Item>{userCurrent.level || ' Should to pass test'}</ListGroup.Item>
                                        </ListGroup>
                                    </Card.Body>
                                </Accordion.Collapse>
                            </Card>

                            <Card>
                                <Card.Header>
                                    <Accordion.Toggle as={Button} variant="link" eventKey="1">
                                        Info about user location
                                    </Accordion.Toggle>
                                </Card.Header>
                                <Accordion.Collapse eventKey="1">
                                    <Card.Body className ="col-sm-12">
                                        <ListGroup className ="col-sm-6 nopadding">
                                            <ListGroup.Item>Email:</ListGroup.Item>
                                            <ListGroup.Item>Phone:</ListGroup.Item>
                                            <ListGroup.Item>Address:</ListGroup.Item>
                                            <ListGroup.Item>Place:</ListGroup.Item>
                                            <ListGroup.Item>Unit:</ListGroup.Item>
                                            <ListGroup.Item>Unit Manager:</ListGroup.Item>
                                            <ListGroup.Item>HR:</ListGroup.Item>
                                        </ListGroup>
                                        <ListGroup className ="col-sm-6 nopadding"> 
                                            <ListGroup.Item>{userCurrent.email}</ListGroup.Item>
                                            <ListGroup.Item>{userCurrent.phone}</ListGroup.Item>
                                            <ListGroup.Item>{userCurrent.address}</ListGroup.Item>
                                            <ListGroup.Item>{userCurrent.place}</ListGroup.Item>
                                            <ListGroup.Item>{userCurrent.unit}</ListGroup.Item>
                                            <ListGroup.Item>{userCurrent.unitManager}</ListGroup.Item>
                                            <ListGroup.Item>{userCurrent.hr}</ListGroup.Item>
                                        </ListGroup>
                                    </Card.Body>
                                </Accordion.Collapse>
                            </Card>
                        </Accordion>
                    </Card>
                </Card>
            </section>
            <section className ='col-sm-4 white'>

                {newAccount ? (
                    <ChatCreate 
                        stepsStart = {stepsStart } 
                        userCurrent = {userCurrent} 
                        setUserCurrent = {setUserCurrent}
                        idUser = {id}
                    />
                ):(
                    // <ChatChange userCurrent = {userCurrent} setUserCurrent = {setUserCurrent}/>
                    <></>
                )}
            </section>
        </section>
    );
}

export default Account;
