import { Component } from 'react';
import ChatBot from 'react-simple-chatbot';
import PropTypes from 'prop-types';

const ChatCreate = (props) => {
    // const { stepsStart } = props;
    // const {  duration, front_options, phone,address } = stepsStart;

    const handleEnd = ({ steps, values }) => {
        console.log(steps);
        console.log(values);
        alert(`Chat handleEnd callback! Number: ${values[0]}`);
      }

    return (
        <ChatBot
          steps={stepsStart}
          handleEnd={handleEnd}
        />
    );
}
class Review extends Component {
    constructor(props) {
      super(props);

      this.state = {
        name: '',
        gender: '',
        age: '',
      };
    }

    componentWillMount() {
      const { steps } = this.props;
      const {  duration, front_options, phone,address } = steps;

      this.setState({  duration, front_options, phone,address });
    }

    render() {
      const { duration, front_options, phone,address } = this.state;
      return (
        <div style={{ width: '100%' }}>
          <h3>Summary</h3>
          <table>
            <tbody>
              <tr>
                <td>Name</td>
                <td>{duration.value}</td>
              </tr>
              <tr>
                <td>Gender</td>
                <td>{front_options.value}</td>
              </tr>
              <tr>
                <td>Age</td>
                <td>{phone.value}</td>
              </tr>
              <tr>
                <td>Age</td>
                <td>{address.value}</td>
              </tr>
            </tbody>
          </table>
        </div>
      );
    }
  }

  ChatCreate.propTypes = {
    steps: PropTypes.object,
  };

  ChatCreate.defaultProps = {
    steps: undefined,
  };

  class SimpleForm extends Component {
    handleEnd({ steps, values }) {
        console.log(steps);
        console.log(values);
        alert(`Chat handleEnd callback! Number: ${values[0]}`);
      }

    render() {
      return (
        <ChatBot
        //   steps={stepsStart}
          handleEnd={this.handleEnd}
        />
      );
    }
  }

  export default ChatCreate;

  export const stepsStart = [
    {
        id: '1',
        message: 'What is your global direction?',
        trigger: 'duration',
      },
      {
        id: 'duration',
        options: [
          { value: 'Frontend Developer', label: 'Frontend Developer', trigger: 'front' },
          { value: 'Backend Developer', label: 'Backend Developer', trigger: 'back' },
          { value: 'Testers', label: 'Testers', trigger: 'test' },
        ],
      },
      {
        id: '9',
        user: true,
        trigger: '3',
      },
      {
        id: 'front',
        message: 'What is your key technology?',
        trigger: 'front_options',
      },
      {
        id: 'front_options',
        options: [
          { value: 'ReactJS', label: 'ReactJS', trigger: '2' },
          { value: 'React Native', label: 'React Native', trigger: '2' },
          { value: 'AngularJS', label: 'AngularJS', trigger: '2' },
          { value: 'NodeJS', label: 'NodeJS', trigger: '2' },
          { value: 'Vue', label: 'Vue', trigger: '2' },  
          { value: 'BabelJs', label: 'BabelJs', trigger: '2' },
        ],
      },
      {
        id: 'back',
        message: 'What is your key technology?',
        trigger: 'back_options',
      },
      {
        id: 'back_options',
        options: [
          { value: 'Java', label: 'Java', trigger: '2' },
          { value: '.NET', label: '.NET', trigger: '2' },
          { value: 'PHP', label: 'PHP', trigger: '2' },
          { value: 'Python', label: 'Python', trigger: '2' },
          { value: 'C++', label: 'C++', trigger: '2' },  
          { value: 'Maven', label: 'Maven', trigger: '2' },
        ],
      },
      {
        id: 'test',
        message: 'What is your key technology?',
        trigger: 'test_options',
      },
      {
        id: 'test_options',
        options: [
          { value: 'Automated Test Specialists', label: 'Automated Test Specialists', trigger: '2' },
          { value: 'DBMS testers', label: 'DBMS testers', trigger: '2' },
          { value: 'Test Analysts', label: 'Test Analysts', trigger: '2' },
          { value: 'Developers-testers', label: 'Developers-testers', trigger: '2' },
          { value: 'Testers-DevOPs', label: 'Testers-DevOPs', trigger: '2' },  
        ],
      },
      {
        id: '2',
        message: 'What is your phone?',
        trigger: 'phone',
      },
      {
        id: 'phone',
        user: true,
        trigger: '3',
        validator: (value) => {
          if (isNaN(value)) {
            return 'value must be a number';
          } else if (value < 0) {
            return 'value must be positive';
          } else if (value < 9) {
            return `Your enter incorrect number`;
          }

          return true;
        },
      },
      {
        id: '3',
        message: 'What is your office address?',
        trigger: 'address',
      },
      {
        id: 'address',
        options: [
          { value: `343 Preston Street 11th Floor Ottawa, ON K1S 1N4
          Canada`, 
            label: `343 Preston Street 11th Floor Ottawa, ON K1S 1N4
          Canada`, 
            trigger: '4' },
          { value: `5200 Yonge Street North York, ON M2N 5P6 Canada`, 
