import { useEffect, useState } from 'react';
import { auth, firestore } from './../firebase';
import Chat from './Chat';
import { Accordion, Card, Button, ListGroup,Tabs, Tab } from 'react-bootstrap'
import ChatTech from './ChatTech';
import { stepsStartCreate, stepsTechFront, stepsPsycho, stepsEnglish} from '../logic/chatStart';
import './account.css';

const Account = (props) => {
    const handleLogout = () => {
        setId(0)
        auth.signOut();
      };
    const [name, setName] = useState('');
    const [newAccount, setNewAccount] = useState(true);
    const [key, setKey] = useState('');

    const [userCurrent, setUserCurrent] = useState('');
    const {
        id,
        setId,
    } = props;

    const getDateFromUser = (data) => {
        setUserCurrent(data)
        setName(data.displayName);
        if(data.new) {
            setNewAccount(true);
        } else {
            setNewAccount(false);
        }
    }

    const getUser = () => {
        firestore.collection('users')
            .get()
            .then(snapshot => {
                snapshot.forEach(doc => {
                    if(doc.id === id) {
                        const data = doc.data();
                        getDateFromUser(data);
                    }
                })
            })
            .catch(error => console.log(error))
    }

    useEffect(() => {
        getUser()
      }, []);

    const getPrimary = (value, duration) => {
        if(duration === 'Frontend Developer' ) {
            return 'JavaScript, HTML, CSS';
        } else {
            if(duration === 'Backend Developer') {
                return value;
            } else {
                return 'QA test'
            }
        }
    }

    const getUnit = (duration, unit) => {
        return `Digital Engagement Practice/Digital Technology/
                DEP UA/DEP UA East & West/DEP UA East/${duration}/
                ${unit}`
    }

    const getManager = (value) => {
        switch (value) {
            case 'ReactJS':
            case 'Java':
                case 'Automated Test Specialists': return 'Lochlan Cottrell';
            case 'React Native':
            case '.NET':
                case 'DBMS testers': return 'Yasin Huang';
            case 'AngularJS':
            case 'PHP':
                case 'Test Analysts': return 'Amanpreet Cowan';
            case 'NodeJS':
            case 'Python':
                case 'Developers-testers': return 'Dewi Dunlap';
            case 'Vue':
            case 'C++':
                case 'Testers-DevOPs': return 'Tonya Chang';
            case 'BabelJs': 
            default: return 'Kendall Bolton';
        }
    }

    const getPlace = (value) => {
        return `PC-${parseInt(value)}/`
    }

    const getHR = (value) => {
        switch (value) {
            case 'Frontend Developer': return 'Coral Howarth';
            case 'Backend Developer': return 'Zidane Pate';
            case 'Testers': return 'Bradley Hendrix';
            default: return 'Fraya Hood'
        }
    }

    const handleEndCreate = async ({ steps, values }) => {
        const user = {};
        user.duration = values[1];
        user.profession = values[2];
        user.status = values[0];
        user.address = values[4];
        user.phone = values[3];
        user.displayName = userCurrent.displayName;
        user.new = false;
        user.email = userCurrent.email;
        const primary = user.primary = getPrimary(user.profession, user.duration);
        const unit = user.unit = getUnit(user.duration, user.profession);
        const unitManager = user.unitManager = getManager(user.profession);
        const place = user.place = getPlace(values[4]);
        const hr = user.hr = getHR(values[1]);

        setUserCurrent(user);
        console.log(123, user ,id);
        firestore
        .collection('users')
        .doc(id)
        .update({
            new: false,
            duration : values[1],
            profession : values[2],
            status: values[0],
            address: values[4],
            phone : values[3],
            unit: unit,
            primary: primary,
            unitManager: unitManager,
            place: place,
            hr: hr,
        })
        .then(() => {
            console.log('User updated!');
            setNewAccount(false)
        });
      }

    const handleEndTech = async({ steps, values }) => {
        console.log(steps, values)
    }

    return (
        <section className = 'hero'>
            <nav>
                <h2>Welcome {userCurrent.displayName}</h2>
                <button onClick = {handleLogout}>Logout</button>
            </nav>
            <section>
                <div className="container bootstrap snippets bootdey">
                    <div className="profile-nav col-md-3">
                        <div className="panel">
                            <div className="user-heading round">
                                <a href="#">
                                    <img src="https://bootdey.com/img/Content/avatar/avatar3.png" alt=""/>
                                </a>
                                <h1>Camila Smith</h1>
                                <p>deydey@theEmail.com</p>
                            </div>
                        </div>
                    </div>
                    <div className="profile-info col-md-9">
                        <div className="panel-body pro-graph-info">
                            <h1>Profession Graph</h1>
                            <ul>
                                <li className="pro-row">
                                    <p><span>Status:</span>{userCurrent.status}</p>
                                </li>
                                <li className="pro-row">
                                    <p><span>Job Function:</span>{userCurrent.profession}</p>
                                </li>
                                <li className="pro-row">
                                    <p><span>Primary Skills:</span>{userCurrent.duration}</p>
                                </li>
                                <li className="pro-row">
                                    <p><span>Primary Skills:</span>{userCurrent.duration}</p>
                                </li>
                                <li className="pro-row">
                                    <p><span>Key Skills:</span>{userCurrent.primary}</p>
                                </li>
                                <li className="pro-row">
                                    <p><span>Level:</span>{userCurrent.level || 'Should to pass test'}</p>
                                </li>
                            </ul>
                        </div>
                        <div className="panel-body bio-graph-info">
                            <h1>Bio Graph</h1>
                            <ul>
                                <li className="bio-row">
                                    <p><span>Email:</span>{userCurrent.email}</p>
                                </li>
                                <li className="bio-row">
                                    <p><span>Phone:</span>{userCurrent.phone}</p>
                                </li>
                                <li className="bio-row">
                                    <p><span>Address:</span>{userCurrent.address}</p>
                                </li>
                                <li className="bio-row">
                                    <p><span>Place:</span>{userCurrent.place}</p>
                                </li>
                                <li className="bio-row">
                                    <p><span>Unit:</span>{userCurrent.unit}</p>
                                </li>
                                <li className="bio-row">
                                    <p><span>Unit Manager:</span>{userCurrent.unitManager}</p>
                                </li>
                                <li className="bio-row">
                                    <p><span>HR:</span>{userCurrent.hr}</p>
                                </li>
                            </ul>
                        </div>
                    </div>
                </div>
            </section>
            <section className ='col-sm-8 nopadding'>
                <Card className = 'rowInfo col-sm-12 nopadding'>
                    <Card className = "col-sm-3">
                        <Card.Img variant="button" width="100%" src='avatar.jpeg' />
                        <Card.Body>
                            <Card.Title>{name}</Card.Title>
                            <Card.Text>
                            </Card.Text>
                        </Card.Body>
                    </Card>
                    <Card className = "col-sm-9 white">
                        <Accordion>
                            <Card>
                                <Card.Header>
                                    <Accordion.Toggle as={Button} variant="link" eventKey="0">
                                        Info about user
                                    </Accordion.Toggle>
                                </Card.Header>
                                <Accordion.Collapse eventKey="0">
                                    <Card.Body className ="col-sm-12">
                                        <ListGroup className ="col-sm-6 nopadding">
                                            <ListGroup.Item>Status:</ListGroup.Item>
                                            <ListGroup.Item>Job Function:</ListGroup.Item>
                                            <ListGroup.Item>Primary Skills:</ListGroup.Item>
                                            <ListGroup.Item>Key Skills:</ListGroup.Item>
                                            <ListGroup.Item>Level:</ListGroup.Item>
                                        </ListGroup>
                                        <ListGroup className ="col-sm-6 nopadding"> 
                                            <ListGroup.Item>{userCurrent.status}</ListGroup.Item>
                                            <ListGroup.Item>{userCurrent.profession}</ListGroup.Item>
                                            <ListGroup.Item>{userCurrent.duration}</ListGroup.Item>
                                            <ListGroup.Item>{userCurrent.primary}</ListGroup.Item>
                                            <ListGroup.Item>{userCurrent.level || ' Should to pass test'}</ListGroup.Item>
                                        </ListGroup>
                                    </Card.Body>
                                </Accordion.Collapse>
                            </Card>

                            <Card>
                                <Card.Header>
                                    <Accordion.Toggle as={Button} variant="link" eventKey="1">
                                        Info about user location
                                    </Accordion.Toggle>
                                </Card.Header>
                                <Accordion.Collapse eventKey="1">
                                    <Card.Body className ="col-sm-12">
                                        <ListGroup className ="col-sm-6 nopadding">

                                        </ListGroup>
                                    </Card.Body>
                                </Accordion.Collapse>
                            </Card>
                        </Accordion>
                    </Card>
                </Card>
            </section>
            <section className ='col-sm-4 '>

                {newAccount ? (
                    <ChatTech
                        userCurrent = {userCurrent} 
                        setUserCurrent = {setUserCurrent}
                        idUser = {id}
                        stepsStart = {stepsStartCreate}
                        handleEnd = {handleEndCreate }
                        setNewAccount = {setNewAccount}
                    />
                ):(
                    <Tabs
                        id="controlled-tab-example"
                        activeKey={key}
                        onSelect={(k) => setKey(k)}
                        className = "red"
                        >
                        <Tab eventKey="change" title="Change user info">
                           {/* <ChatChange
                            userCurrent = {userCurrent} 
                            setUserCurrent = {setUserCurrent}
                            idUser = {id}/> */}
                        </Tab>
                        <Tab eventKey="tech" title="Pass technical test">
                            <ChatTech
                                    userCurrent = {userCurrent} 
                                    setUserCurrent = {setUserCurrent}
                                    idUser = {id}
                                    stepsStart = {stepsTechFront}
                                    handleEnd = {handleEndTech}
                                />
                        </Tab>
                        <Tab eventKey="english" title="Pass English test">
                            <ChatTech
                                    userCurrent = {userCurrent} 
                                    setUserCurrent = {setUserCurrent}
                                    idUser = {id}
                                    stepsStart = {stepsEnglish}
                                    handleEnd = {handleEndTech}
                                />
                        </Tab>
                        <Tab eventKey="psychological" title="Pass psychological test">
                            <ChatTech
                                    userCurrent = {userCurrent} 
                                    setUserCurrent = {setUserCurrent}
                                    idUser = {id}
                                    stepsStart = {stepsPsycho}
                                    handleEnd = {handleEndTech}
                                />
                        </Tab>
                        <Tab eventKey="task" title="Set a task">
                            24124
                        </Tab>
                        <Tab eventKey="meet" title="Schedule a meeting">
                            24124
                        </Tab>
                    </Tabs>
                )}
            </section>
        </section>
    );
}

export default Account;
