import { Component } from 'react';
import ChatBot from 'react-simple-chatbot';
import PropTypes from 'prop-types';

class Review extends Component {
    constructor(props) {
      super(props);

      this.state = {
        name: '',
        gender: '',
        age: '',
      };
    }

    componentWillMount() {
      const { steps } = this.props;
      const {  duration, front_options, phone,address } = steps;

      this.setState({  duration, front_options, phone,address });
    }

    render() {
      const { duration, front_options, phone,address } = this.state;
      return (
        <div style={{ width: '100%' }}>
          <h3>Summary</h3>
          <table>
            <tbody>
              <tr>
                <td>Name</td>
                <td>{duration.value}</td>
              </tr>
              <tr>
                <td>Gender</td>
                <td>{front_options.value}</td>
              </tr>
              <tr>
                <td>Age</td>
                <td>{phone.value}</td>
              </tr>
              <tr>
                <td>Age</td>
                <td>{address.value}</td>
              </tr>
            </tbody>
          </table>
        </div>
      );
    }
  }

  Review.propTypes = {
    steps: PropTypes.object,
  };

  Review.defaultProps = {
    steps: undefined,
  };

  class SimpleForm extends Component {
    render() {
      return (
        <ChatBot
          steps={stepsStart}
        />
      );
    }
  }

  export default SimpleForm;

export const stepsStart = [
    {
        id: '1',
        message: 'What is your global direction?',
        trigger: 'duration',
      },
      {
        id: 'duration',
        options: [
          { value: 1, label: 'Frontend Developer', trigger: 'front' },
          { value: 2, label: 'Backend Developer', trigger: 'back' },
          { value: 3, label: 'Testers', trigger: 'test' },
        ],
      },
      {
        id: '9',
        user: true,
        trigger: '3',
      },
      {
        id: 'front',
        message: 'What is your key technology?',
        trigger: 'front_options',
      },
      {
        id: 'front_options',
        options: [
          { value: 1, label: 'ReactJS', trigger: '2' },
          { value: 2, label: 'React Native', trigger: '2' },
          { value: 3, label: 'AngularJS', trigger: '2' },
          { value: 4, label: 'NodeJS', trigger: '2' },
          { value: 5, label: 'Vue', trigger: '2' },  
          { value: 6, label: 'BabelJs', trigger: '2' },
        ],
      },
      {
        id: 'back',
        message: 'What is your key technology?',
        trigger: 'back_options',
      },
      {
        id: 'back_options',
        options: [
          { value: 1, label: 'Java', trigger: '2' },
          { value: 2, label: '.NET', trigger: '2' },
          { value: 3, label: 'PHP', trigger: '2' },
          { value: 4, label: 'Python', trigger: '2' },
          { value: 5, label: 'C++', trigger: '2' },  
          { value: 6, label: 'Maven', trigger: '2' },
        ],
      },
      {
        id: 'test',
        message: 'What is your key technology?',
        trigger: 'test_options',
      },
      {
        id: 'test_options',
        options: [
          { value: 1, label: 'Automated Test Specialists', trigger: '2' },
          { value: 2, label: 'DBMS testers', trigger: '2' },
          { value: 3, label: 'Test Analysts', trigger: '2' },
          { value: 4, label: 'Developers-testers', trigger: '2' },
          { value: 5, label: 'Testers-DevOPs', trigger: '2' },  
        ],
      },
      {
        id: '2',
        message: 'What is your phone?',
        trigger: 'phone',
      },
      {
        id: 'phone',
        user: true,
        trigger: '3',
        validator: (value) => {
          if (isNaN(value)) {
            return 'value must be a number';
          } else if (value < 0) {
            return 'value must be positive';
          } else if (value < 9) {
            return `Your enter incorrect number`;
          }

          return true;
        },
      },
      {
        id: '3',
        message: 'What is your office address?',
        trigger: 'address',
      },
      {
        id: 'address',
        options: [
          { value: 1, label: `343 Preston Street 11th Floor Ottawa, ON K1S 1N4
          Canada`, trigger: '4' },
          { value: 2, label: `5200 Yonge Street North York, ON M2N 5P6 Canada`, trigger: '4' },
          { value: 3, label: `110 110th Ave. NE Suite 310 Bellevue, WA 98004 USA`, trigger: '4' },
        ],
      },
      {
        id: '4',
        message: 'Аre you a member of any project?',
        trigger: '4_options',
      },
      {
        id: '4_options',
        options: [
            { value: 'yes', label: 'Yes', trigger: 'projectOptions' },
            { value: 'no', label: 'No', trigger: 'end' },
        ],
      },
      {
        id: 'projectOptions',
        message: 'Аre you a member of any project?',
        trigger: '1',
      },
      {
        id: 'end',
        message: 'Thanks! Your data was submitted successfully!',
        component: <Review />,
        asMessage: true,
        end: true,
      },
];
