import { Component } from 'react';
import ChatBot from 'react-simple-chatbot';
import PropTypes from 'prop-types';

const ChatCreate = (props) => {
    const { stepsStart } = props;
    const {  duration, front_options, phone,address } = stepsStart;

    const handleEnd = ({ steps, values }) => {
        console.log(steps);
        console.log(values);
        alert(`Chat handleEnd callback! Number: ${values[0]}`);
      }

    return (
        <ChatBot
          steps={stepsStart}
          handleEnd={handleEnd}
        />
    );
}
class Review extends Component {
    constructor(props) {
      super(props);

      this.state = {
        name: '',
        gender: '',
        age: '',
      };
    }

    componentWillMount() {
      const { steps } = this.props;
      const {  duration, front_options, phone,address } = steps;

      this.setState({  duration, front_options, phone,address });
    }

    render() {
      const { duration, front_options, phone,address } = this.state;
      return (
        <div style={{ width: '100%' }}>
          <h3>Summary</h3>
          <table>
            <tbody>
              <tr>
                <td>Name</td>
                <td>{duration.value}</td>
              </tr>
              <tr>
                <td>Gender</td>
                <td>{front_options.value}</td>
              </tr>
              <tr>
                <td>Age</td>
                <td>{phone.value}</td>
              </tr>
              <tr>
                <td>Age</td>
                <td>{address.value}</td>
              </tr>
            </tbody>
          </table>
        </div>
      );
    }
  }

  ChatCreate.propTypes = {
    steps: PropTypes.object,
  };

  ChatCreate.defaultProps = {
    steps: undefined,
  };

  class SimpleForm extends Component {
    handleEnd({ steps, values }) {
        console.log(steps);
        console.log(values);
        alert(`Chat handleEnd callback! Number: ${values[0]}`);
      }

    render() {
      return (
        <ChatBot
        //   steps={stepsStart}
          handleEnd={this.handleEnd}
        />
      );
    }
  }

  export default ChatCreate;
