import { useEffect, useState } from 'react';
import './App.css';
import { auth } from './firebase';
import Login from './component/Login';
import Account from './component/Account';

function App() {
  const [user, setUser] = useState('');

  const authListener = () => {
    auth.onAuthStateChanged(user => {
      if(user) {
        setUser(user);
      } else {
        setUser('');
      }
    })
  };

  useEffect(() => {
    authListener();
  }, []);

  return (
    <div className="App">
      {user ? (
        <Account/>
      ) : (
        <Login/>
      )}
    </div>
  );
}

export default App;
