import { useEffect, useState } from 'react';
import { auth, firestore } from './../firebase';
import Chat from './Chat';
import { Accordion, Card, Button } from 'react-bootstrap'

const Account = (props) => {
    const handleLogout = () => {
        auth.signOut();
      };
    const [name, setName] = useState('');
    const [userCurrent, setUserCurrent] = useState('');
    const {
        id,
        setId,
    } = props;

    const getUser = () => {
        firestore.collection('users')
            .get()
            .then(snapshot => {
                snapshot.forEach(doc => {
                    const data = doc.data();
                    setUserCurrent(data);
                })
            })
            .catch(error => console.log(error))
    }

    useEffect(() => {
        getUser()
      }, []);

    return (
        <section className = 'hero'>
            <nav>
                <h2>Welcome {userCurrent.displayName}</h2>
                <button onClick = {handleLogout}>Logout</button>
            </nav>
            <section className ='col-sm-8 nopadding'>
                <Card className = 'rowInfo col-sm-12 nopadding'>
                    <Card className = "col-sm-3">
                        <Card.Img variant="button" width="100%" src='avatar.jpeg' />
                        <Card.Body>
                            <Card.Title>Card Title</Card.Title>
                            <Card.Text>
                            {/* Some quick example text to build on the card title and make up the bulk of
                            the card's content. */}
                            </Card.Text>
                        </Card.Body>
                    </Card>
                    <Card className = "col-sm-9 white">
                        <Accordion>
                            <Card>
                                <Card.Header>
                                <Accordion.Toggle as={Button} variant="link" eventKey="0">
                                    Click me!
                                </Accordion.Toggle>
                                </Card.Header>
                                <Accordion.Collapse eventKey="0">
                                <Card.Body>Hello! I'm the body</Card.Body>
                                </Accordion.Collapse>
                            </Card>
                        </Accordion>
                    </Card>
                </Card>
            </section>
            <section className ='col-sm-4 white'>
                <Chat/>
            </section>
        </section>
    );
}

export default Account;
